package main;

public class Main7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int numero= 18;
		int contarMostrar=1;
		int contador=1;
		while(contarMostrar<=numero) {
			for(int i=0;i<contador&&contarMostrar<=numero;i++) {
				System.out.print(contarMostrar+" ");
				contarMostrar++;
			}
			System.out.println();
			contador++;
			
		}
		
		

	}

}
